INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('1', 'RD', '1', 'ACTIVO', 'A', 'G', 'false', 'false', '0.00', '0.00', 'Representa los bienes y derechos patrimoniales originados en transacciones o hechos contables propios del Ente, capaces de producir ingresos económicos-financieros, razonablemente esperados durante el desarrollo de su gestión y que contribuyen directa o indirectamente al cumplimiento de sus objetivos.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('2', 'RD', '1.1', 'ACTIVO CORRIENTE', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el efectivo y los valores susceptibles de convertirse en dinero o consumirse en un plazo inferior o igual a doce meses, así como la existencia de bienes depositados en los almacenes.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('3', 'RD', '1.1.01', 'DISPONIBILIDADES', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Comprende la existencia en poder del Ente de moneda de curso legal, moneda extranjera, cheques, giros bancarios y postales a la vista, depósitos sin restricciones especificas en entidades bancarias de cual puede disponer el Ente para proceder al pago por la adquisición de bienes y servicios así como al pago de sus deudas interna o externa y la ejecución, si fuere de lugar, de inversiones.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('4', 'RD', '1.1.01.01', 'CAJA', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los fondos del ejercicio presupuestario, para cancelar gastos en efectivo o mediante cheques, de conformidad con las previsiones del respectivo reglamento y los remanentes de ingresos no depositados en las instituciones financieras.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('5', 'RD', '1.1.01.01.002', 'CAJA CHICA', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los fondos entregados durante el ejercicio presupuestario a los funcionarios autorizados por el Ente contable, para cancelar gastos en efectivo o mediante cheques, de conformidad con las previsiones del respectivo reglamento de Fondo Reponible y Caja Chica.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('6', 'RD', '1.1.01.02', 'BANCOS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el movimiento de los fondos depositados en la Institución Financiera Oficial y otras instituciones bancarias, con las cuales se hayan celebrado convenios para la recaudación de los ingresos públicos, de la venta de activos o por otros conceptos; así como el de los desembolsos efectuados en calidad de préstamo por las instituciones financieras, comerciales e industriales, nacionales, extranjeras, con cargo a los fondos enunciados. Por medio de estas cuentas se efectúa el pago de los gastos corrientes y de capital, transferencias, constitución y reposición de Transferencias a Pagadores, Fondos Reponibles, en fideicomiso y cualesquiera otros tipos de gastos con cargo a la Tesorería Nacional.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('7', 'RD', '1.1.01.03', 'DISPONIBILIDADES EN PODER DE LAS DAFs', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el movimiento de los fondos administrados exclusivamente por las Instituciones y Poderes depositados en Instituciones Financieras Oficiales y otras instituciones bancarias, con las cuales se hayan celebrado convenios. Por medio de estas cuentas se efectúan: la recaudación de ingresos y los pagos de los gastos corrientes, de capital, transferencias y cualesquiera otros tipos de gastos con cargo a las cuentas de las Instituciones. Se incluyen los fondos que la Tesorería Nacional deposita en las cuentas bancarias de las instituciones por concepto de fondos reponibles y fondos en avances.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('8', 'RD', '1.1.01.04', 'BANCOS - FONDOS DE TERCEROS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los fondos depositados en entidades bancarias destinados a cancelar las garantías a los proveedores y contratistas, las cuales fueron por ellos otorgadas o practicadas por las Instituciones o la Tesorería Nacional a los fines de garantizar el fiel cumplimiento de las obligaciones contractuales y atender a la eventualidad de sus reintegros. Incluye el monto de anticipos otorgados por los contratistas o proveedores destinados al proceso licitatorio y cuyo importe debe ser reintegrado. También incluye montos depositados en el Tesoro, por concepto de litigio entre privados.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('10', 'RD', '1.1.02', 'INVERSIONES FINANCIERAS A CORTO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las colocaciones de excedentes de fondos de la Tesorería Nacional en títulos y valores, cuya recuperación se efectuará en un plazo inferior o igual a doce meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('9', 'RD', '1.1.01.05', 'TRANSFERENCIAS PARA PAGOS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los fondos que agrupa las transferencias que la Tesorería Nacional efectúa a las Instituciones para atender la cancelación de gastos. Las sub cuentas identifican a las DAFs o a los respectivos responsables de Pagos.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('11', 'RD', '1.1.02.01', 'DEPÓSITOS A PLAZO FIJO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las colocaciones de excedentes de fondos de la Tesorería Nacional en certificados financieros a plazos específicos con vencimiento en doce meses o menos.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('12', 'RD', '1.1.02.02', 'TÍTULOS Y VALORES', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las colocaciones de excedentes de fondos de la Tesorería Nacional, en documentos tales como BONOS, cuyo vencimiento es igual o menor a un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('13', 'RD', '1.1.02.03', 'INVERSIONES TEMPORARIAS A CLASIFICAR', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto invertido por el Ente, a plazo igual o menor de un año, cuyo concepto de inversión aún no está definido.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('14', 'RD', '1.1.02.04', 'FONDOS Y BIENES EN FIDEICOMISOS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los fondos colocados en fondos de inversiones de acuerdo con convenios de fideicomisos celebrados, destinados a la adquisición de bienes, servicio o pagos. Su saldo representa el monto de la transferencia de bienes con el objeto de dar cumplimiento a la ejecución de la política de privatizaciones y otros contratos de fideicomiso que celebre El Estado.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('15', 'RD', '1.1.02.98', 'OTRAS INVERSIONES', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las colocaciones de excedentes hechos por la Tesorería Nacional con vencimiento en plazo igual o menor a un año, no incluida en ninguna de las cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('16', 'RD', '1.1.03', 'RENTAS POR RECAUDAR', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los ingresos públicos devengados por concepto de impuestos y contribuciones especiales, liquidados a nombre de los contribuyentes, también incluye rentas de la propiedad y otras rentas no tributarias, por intereses que generen los depósitos realizados en las instituciones financieras públicas o privadas u originados por la posesión de títulos y valores y, el de los ingresos originados en operaciones de carácter extraordinario pendientes de recaudación y cuyo cobro se prevé realizar en corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('17', 'RD', '1.1.03.01', 'DE ORIGEN TRIBUTARIO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los ingresos públicos devengados por concepto de impuestos liquidados a nombre de los contribuyentes pendiente de recibir por el Ente, cuyas fuentes originarias son los Órganos Recaudadores del Estado y cuya recuperación se efectuará a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('18', 'RD', '1.1.03.02', 'DE ORIGEN NO TRIBUTARIO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los ingresos públicos devengados por conceptos tales como: venta de activos, intereses sobre préstamos o inversiones, etc. los cuales no han sido recibidos por el Ente, pero que se espera recuperar en corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('19', 'RD', '1.1.04', 'CUENTAS Y DOCUMENTOS POR COBRAR A CORTO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto a favor del Ente, originado en derechos adquiridos de terceros por la explotación de bienes de cualquier naturaleza, servicios o actividades análogas. Se generan en el momento en que se formalizan los acuerdos que los originan, y se cancelan cuando se produce el cobro, conforme con las cláusulas contractuales previstas. Deberán incluirse los pagos anticipados en concepto de gastos, servicios o bienes que se recibirán en ejercicios siguientes.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('20', 'RD', '1.1.04.01', 'ANTICIPOS FINANCIEROS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los anticipos otorgados por la Tesorería Nacional o girados por el Ente contable a los responsables de los mismos. Incluye los anticipos a cuenta de recaudación girados a Municipios, todo ello exigibles a corto plazo, así como los anticipos de fondos en avances y fondos reponibles.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('21', 'RD', '1.1.04.02', 'CUENTAS A COBRAR A CORTO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los derechos de cobros a terceros a favor del Ente contable, cuyo origen sea distinto de las rentas por recaudar y documentos por cobrar que serán exigibles a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('22', 'RD', '1.1.04.03', 'DOCUMENTOS A COBRAR A CORTO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los derechos de cobro del Ente contable respaldados en documentos mercantiles negociables, que serán exigibles a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('23', 'RD', '1.1.04.04', 'PRESTAMOS DIRECTOS OTORGADOS A CORTO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los derechos de cobros del ente contable respaldados en documentos pactados con terceros, generados por formalización de préstamos, que serán exigibles a corto plazo, anticipos financieros de fondos otorgados por la Tesorería Nacional.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('24', 'RD', '1.1.04.05', 'PRESTAMOS INDIRECTOS OTORGADOS A CORTO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones de cobros del Ente contable, por cuenta de préstamos otorgados por terceras a instituciones públicas o privadas, que serán exigibles a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('25', 'RD', '1.1.04.06', 'TÍTULOS Y VALORES POR COBRAR A CORTO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto exigible por el Estado, generado por la venta de títulos o valores con valor negociable, vendidos a terceros a crédito.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('26', 'RD', '1.1.04.07', 'GASTOS PAGADOS POR ADELANTADO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto adelantado por el Ente a proveedores, cuyo bien o servicio será recibido en tiempo diferido menor o igual a un año, de acuerdo a cláusulas contractuales.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('27', 'RD', '1.1.06', 'INVENTARIOS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el valor de las existencias de mercancías, materiales, suministros, repuestos y herramientas mayores; depositados en las proveedurías y almacenes del organismo; también incluye los semovientes y bienes destinados al consumo o al uso, cuya asignación aún no ha sido destinada para la venta.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('28', 'RD', '1.1.06.01', 'EXISTENCIAS BIENES DE CAMBIO Y CONSUMO.', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto propiedad del Ente conformado por bienes adquiridos, para fines de consumo o venta. Deben valuarse al costo de adquisición, incluyendo todo tipo de egresos necesario para llevarlo a su punto de venta o de consumo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('29', 'RD', '1.1.06.02', 'EXISTENCIAS DE PRODUCTOS TERMINADOS Y EN PROCESO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Comprende el monto de los productos terminados adquiridos o de elaboración propia, disponibles para su enajenación o que se encuentren en etapa de procesamiento. Deben valuarse al costo de adquisición, incluyendo todo tipo de egresos necesario para llevarlo a su punto de venta o de consumo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('30', 'RD', '1.1.06.99.999', 'PREVISIÓN PARA PERDIDAS DE INVENTARIOS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto que preventivamente se carga contra las operaciones del Ente, debido a estimados de pérdida en inventarios por obsolescencia, mermas, derrames, descomposición, etc.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('31', 'RD', '1.1.98', 'OTROS ACTIVOS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de otros bienes y derechos; propiedad del Ente contable, que razonablemente se espera se convierta en efectivo en un período menor o igual a un año, no contabilizados en las cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('32', 'RD', '1.1.98.01', 'FALTANTES POR INVESTIGAR', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los faltantes de fondos girados a funcionarios responsables de su manejo, mientras se determina el uso dado a los mismos, en las dependencias del Ente contable.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('33', 'RD', '1.1.98.02', 'OTROS FONDOS POR COBRAR', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los fondos que deben devolver particulares y que fueron girados durante el ejercicio presupuestario vigente, mediante órdenes de pago.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('34', 'RD', '1.1.98.03', 'ACTIVOS DIFERIDOS A CORTO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto desembolsado por el Ente, cuya prestación de servicio u obtención del bien correspondiente se percibe parcial o totalmente en fecha posterior al desembolso, pero en plazo menor o igual a un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('35', 'RD', '1.1.98.98', 'ACTIVOS CORRIENTES DIVERSOS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los bienes y derechos del Ente contable que razonablemente se espera percibir a corto plazo, no clasificado en las cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('36', 'RD', '1.2', 'ACTIVO NO CORRIENTE', 'A', 'G', 'false', 'false', '0.00', '0.00', 'Representa el monto del grupo de cuentas representativas de bienes y derechos que pueden convertirse en efectivo, en un período mayor al ejercicio fiscal.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('37', 'RD', '1.2.01', 'RENTAS A RECAUDAR A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los ingresos públicos devengados por concepto de impuestos pendientes de cobro, para cuya cancelación excepcionalmente la Administración Tributaria, conforme a las disposiciones legales que regulan la materia, otorga plazo que excede la vigencia del ejercicio presupuestario en curso. También incluye el monto de la venta de bienes cuyo cobro, de acuerdo con previsiones legales aplicables, es convenido por la Administración con el deudor, para que se efectúe en futuros ejercicios presupuestarios.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('38', 'RD', '1.2.02', 'CRÉDITOS A COBRAR A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto por derechos de cobros del Ente, originados en obligaciones contraídos con terceros en la explotación de bienes de cualquier naturaleza, servicios o actividades análogas. Se generan en el momento en que se formalizan los acuerdos que los originan, y se cancelan cuando se produce el cobro, conforme con las cláusulas contractuales previstas. Su vencimiento es mayor que un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('39', 'RD', '1.2.02.01', 'ANTICIPOS A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto por pagos realizados a proveedores y contratistas con carácter de adelanto o con cargo de rendición o devolución de fondos en un plazo mayor que el periodo fiscal.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('40', 'RD', '1.2.02.02', 'CUENTAS POR COBRAR A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los derechos de cobro a terceros a favor del Ente contable, cuyo origen sea distinto de las rentas por recaudar, que serán exigibles en un plazo superior a doce meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('41', 'RD', '1.2.02.03', 'DOCUMENTOS POR COBRAR A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los derechos de cobro del ente contable respaldados en documentos mercantiles negociables, que serán exigibles en un plazo superior a doce meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('42', 'RD', '1.2.02.04', 'PRESTAMOS OTORGADOS A COBRAR A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los derechos de cobro a favor del Ente contable, originados en préstamos concedidos por el Estado a cobrar en el Largo Plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('43', 'RD', '1.2.02.98', 'OTROS EXIGIBLES A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de derechos de cobros del Ente a cobrar en fecha posterior a un año y no incluidos en ninguna de las cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('44', 'RD', '1.2.02.99.999', 'PREVISIÓN PARA INCOBRABLES A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Valores aprovisionados cargados a gastos, por contingencias de incobrabilidad estimados respecto de los saldos a cobrar largo plazo, existentes a una fecha determinada.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('45', 'RD', '1.2.03', 'INVERSIONES FINANCIERAS A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las colocaciones de excedentes de fondos de la Tesorería Nacional en títulos y valores cuya recuperación se efectuará en fecha posterior, mayor de un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('46', 'RD', '1.2.03.01', 'PARTICIPACIONES DE CAPITAL', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto invertido por el Ente en el capital accionario de otra Entidad.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('47', 'RD', '1.2.03.02', 'PATRIMONIO NETO DE INSTITUCIONES DESC. Y AUTÓNOMAS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto invertido por el Ente contable en el capital de Descentralizadas y Autónomas.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('48', 'RD', '1.2.03.03', 'TÍTULOS Y VALORES A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las colocaciones de excedentes de fondos de la Tesorería Nacional, en documentos tales como BONOS, cuyo vencimiento es superior a un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('49', 'RD', '1.2.03.98', 'OTRAS INVERSIONES A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las colocaciones de excedentes de fondos de la Tesorería Nacional, en conceptos distintos a las cuentas citadas, cuyo vencimiento es superior a un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('50', 'RD', '1.2.06', 'BIENES DE USO (ACTIVOS NO FINANCIEROS)', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los bienes muebles e inmuebles construidos, adquiridos o recibidos en donación, que poseen vida útil superior a un año y el de semovientes que son utilizados por el ente contable en los fines que le son propios.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('51', 'RD', '1.2.06.01', 'MAQUINARIAS, EQUIPOS Y MOBILIARIOS.', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto por compra de equipos y sus adiciones y reparaciones extraordinarias realizadas. Constituyen equipos, muebles y máquinas de oficina, vehículos maquinarias, equipos de producción, equipo médico, herramientas mayores, así como animales utilizados en labores educacionales, recreativas o trabajo pesado. Estos equipos son bienes durables para la producción, no se consumen rápidamente en el proceso como los materiales y otros artículos. El término equipo incluye a objetos que reúnen tres características que los distinguen de materiales y suministros: largo uso y duración, sujeto a control de inventario como equipo fijo y sujeto a depreciación. Por largo uso y duración se entiende una vida útil de tres años o más. .Algunos objetos de uso frecuente, como platos y utensilios de cocina, pueden durar varios años, sin embargo; por el hecho de quebrarse o romperse con facilidad, no se consideran equipos. Los artículos como la ropa, que se deterioran rápidamente deben clasificarse co');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('52', 'RD', '1.2.06.02', 'INMUEBLES', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Comprende el valor de las construcciones e instalaciones levantadas sobre los terrenos propiedad del ente, destinada al uso o la producción de bienes y servicios públicos. El concepto edificio incluye todas las instalaciones unidas permanentemente y que forman parte del mismo, las cuales no pueden instalarse o removerse sin romper las paredes, techos o pisos de la edificación. Incluye edificios para oficinas públicas, edificios y locales para escuelas, edificios y casas de habitación y otros no especificados. Incluye el valor de costo de los terrenos propiedad del ente, adquiridos con la finalidad de la construcción de instalaciones destinadas al uso, terrenos para edificios, casas de habitación, locales, construcción de carreteras, aeropuertos, adquisición de terrenos y latifundios destinados a colonización o reforma agraria, incluye además, los predios urbanos, minas, salinas y bienes expropiados.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('53', 'RD', '1.2.06.03', 'CONSTRUCCIONES Y MEJORAS EN PROCESO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto destinado a atender la construcción de obras públicas de beneficio local, regional o nacional, mejoras, ampliaciones y reparaciones de carácter estructural, incluye los gastos de supervisión e inspección de las construcciones realizadas por terceras personas. Se entiende por mejoras, ampliaciones y reparaciones, aquellas que tengan como finalidad mantener o reponer los bienes deteriorados, de forma que puedan seguir siendo utilizadas y prolongar la vida útil del bien, de tal manera que aumente la eficacia en la cobertura de las necesidades derivadas de la prestación de servicios.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('54', 'RD', '1.2.06.04', 'CONSTRUCCIONES POR CONCESIONES EN PROCESO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el valor de los costos incurridos por el Ente para la construcción de obras de infraestructura a través de la modalidad de concesiones a terceros. El cual consiste en la construcción de obras por entidades privadas, con derecho a uso a fin de recuperar la inversión.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('55', 'RD', '1.2.06.98', 'OTROS BIENES DE USO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de aquellos bienes de uso que no han sido incluidos en las cuentas antes descriptas, aplicables a las operaciones del Ente.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('56', 'RD', '1.2.06.99.999', 'AMORTIZACIÓN DE BIENES DE USO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto cargado a gastos por la desvalorización por efecto de uso de los bienes muebles e inmuebles propiedad del Ente; durante la vida útil de los mismos. Estos valores constituyen un porcentaje del valor del bien y conforman una contra cuenta que permite establecer el valor actual histórico del bien.
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('57', 'RD', '1.2.08', 'BIENES INTANGIBLES', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el valor real de activos especiales, no corporales, Ejs. Plusvalía, Derechos de Autor, Patentes, etc.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('58', 'RD', '1.2.08.01', 'ACTIVOS INTANGIBLES', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representan el monto o valor de activos o bienes propiedad del Ente, que no obstante su inmaterialidad corporal constituye en sí mismos un valor económico real.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('59', 'RD', '1.2.08.99.999', 'AMORTIZACIÓN DE BIENES INTANGIBLES', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de la cuenta de valuación de los activos intangibles; integra los montos de los ejercicios fiscales años tras años.
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('60', 'RD', '1.2.98', 'OTROS ACTIVOS NO CORRIENTES', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de otros bienes y derechos; propiedad del Ente contable, que razonablemente se espera se conviertan en efectivo en un período mayor a un año y no contabilizados en las cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('61', 'RD', '1.2.98.01', 'GASTOS PAGADOS POR ADELANTADO A LARGO PLAZO', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto adelantado por el Ente a Terceros por servicios o bienes que recibirá posteriormente y en plazo superior a un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('62', 'RD', '1.2.98.98', 'ACTIVOS DIVERSOS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto total de los bienes y derechos del Ente, no incluidos en las cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('63', 'RD', '1.2.98.98.001', 'ACTIVOS DIFERIDOS', 'A', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de otros bienes y derechos; propiedad del Ente contable, que razonablemente se espera se conviertan en efectivo en un período mayor a un año y no contabilizados en las cuentas anteriores y cuyo efecto se recibirá en fecha posterior al desembolso que originan.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('64', 'RD', '2', 'PASIVOS', 'PA', 'G', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones cuantificables del Ente contraídas con terceros, provenientes de la transferencia de activos o de la proporción de servicios; quedando así afectados parte de sus recursos en función de los vencimientos a operarse, incluyendo asimismo, contingencias con alto grado de probabilidades de que se operen.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('65', 'RD', '2.1', 'PASIVO CORRIENTE', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones para el ente, las cuales deberá cumplir a corto plazo, es decir, en un término igual o menor a un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('67', 'RD', '2.1.01.01', 'GASTOS DE PERSONAL POR PAGAR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Comprende el monto adeudado al personal del ente, por concepto de gasto devengado correspondiente a sueldos, salarios y otras remuneraciones, bonos y complementos, beneficios socioeconómicos y otros gastos. Comprende además, el monto de los aportes  patronales destinados a atender financiamiento de la seguridad social, asistencia socioeconómica, prestaciones sociales y otras indemnizaciones; así como subsidios para capacitación y adiestramiento.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('66', 'RD', '2.1.01', 'CUENTAS POR PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto adeudado por el Ente, que debe pagar en un plazo igual o menor de un año, generados por bienes o servicios recibidos de terceros para la realización de sus responsabilidades.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('68', 'RD', '2.1.01.02', 'CUENTAS A PAGAR PROVEEDORES', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto que el ente adeuda a los proveedores que le suministran materiales, bienes o servicios; el cual deberá cancelar a corto plazo. La clasificación de esta sub-cuenta auxiliar se relaciona con el auxiliar de beneficiarios.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('69', 'RD', '2.1.01.03', 'CUENTAS A PAGAR CONTRATISTAS', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto que el Ente adeuda a los contratistas por concepto de avances y/o cubicaciones de obras, instalaciones y otros; los cuales debe pagar a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('70', 'RD', '2.1.01.04', 'DEDUCCIONES Y RETENCIONES A PAGAR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las deducciones y retenciones efectuadas a proveedores y a contratistas de bienes y servicios, de conformidad con los señalamientos contenidos en los respectivos contratos y Leyes vigentes. Muestra también, el monto de las deducciones y retenciones sobre las remuneraciones practicadas al personal, cuya cancelación se prevé realizar a corto plazo.');
