INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('73', 'RD', '2.1.01.07', 'FONDOS REPONIBLES A PAGAR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Constituyen adelantos y reposiciones de Fondos Reponibles aprobados y/o devengados a ser pagados por la Tesorería Nacional a las instituciones en un corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('74', 'RD', '2.1.01.08', 'FONDOS EN AVANCES A PAGAR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Constituye adelantos de Fondos en Avances aprobados a ser pagados por la Tesorería Nacional a los Poderes y Organismos Especiales; así como a instituciones autorizadas, a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('75', 'RD', '2.1.01.09', 'APORTES CORRIENTES A PAGAR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Comprende los aportes para gastos corrientes a favor de instituciones del Sector Público, del Sector Privado y del exterior que el ente debe realizar, los cuales estando devengados estarían pendientes de ser pagados a corto plazo, por mandato de reglamentaciones legales.');
