INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('77', 'RD', '2.1.01.11', 'PORCIÓN CORRIENTE DE PASIVO A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Comprende los importes de amortización del servicio de las deudas contraídas por el ente a un plazo mayor de doce (12) meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('78', 'RD', '2.1.01.12', 'TRANSFERENCIAS A PAGAR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Constituyen los desembolsos pendientes de ejecutar por la Tesorería Nacional, ya sea como pago directo a terceros o como cesión de fondos para ser pagados a través de las DAF a corto plazo.');
