INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('84', 'RD', '2.1.03', 'DOCUMENTOS EXTERNOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones documentadas mediante letras, pagares y otro documento negociable a pagar a proveedores o contratistas externos a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('85', 'RD', '2.1.03.01', 'DOCUMENTOS A PAGAR A PROVEEDORES EXTERNOS', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Constituyen las obligaciones amparadas en letras, pagares y otros documentos legales a pagar a los proveedores del exterior, a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('86', 'RD', '2.1.03.02', 'DOCUMENTOS A PAGAR A CONTRATISTAS EXTERNOS', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Constituyen las obligaciones amparadas en letras, pagares y otros documentos legales a pagar a contratistas del exterior, a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('87', 'RD', '2.1.03.98', 'OTROS DOCUMENTOS EXTERNOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representan otros documentos externos a pagar a corto plazo y no especificado en las sub-cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('88', 'RD', '2.1.04', 'PRESTAMOS INTERNOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Deuda contraída con instituciones residentes en el país, por concepto de préstamos otorgados al ente, cuyo vencimiento es en un plazo menor de un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('89', 'RD', '2.1.04.01', 'PRESTAMOS INTERNOS DIRECTOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas directamente por el ente público con personas naturales o jurídicas residentes en el país, por concepto de préstamos a pagar a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('90', 'RD', '2.1.04.02', 'PRESTAMOS INTERNOS INDIRECTOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones por concepto de préstamos contraídas por personas naturales o jurídicas con instituciones de financiamientos, avaladas o asumidas por el Estado, a pagar a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('91', 'RD', '2.1.04.98', 'OTROS PRESTAMOS INTERNOS A PAGAR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones por préstamos internos contraídos por el Estado, no contempladas en las sub. cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('92', 'RD', '2.1.05', 'PRESTAMOS EXTERNOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los préstamos asumidos o contraídos con personas naturales o jurídicas en el exterior, con vencimiento a un plazo menor o igual a un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('93', 'RD', '2.1.05.01', 'PRESTAMOS EXTERNOS DIRECTOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa los préstamos contraídas por el Estado con instituciones, organismos y gobiernos extranjeros, con vencimiento menor de un ano.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('94', 'RD', '2.1.05.02', 'PRESTAMOS EXTERNOS INDIRECTOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por personas naturales o jurídicas residentes en el país ante fuentes externas de financiamiento asumidas o avaladas por el Estado con vencimiento a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('95', 'RD', '2.1.06', 'TÍTULOS Y VALORES A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Estado con personas naturales o jurídicas residentes en el país y el exterior, representadas en bonos y otros títulos de valores, con vencimiento inferior a doce meses contados a partir de la fecha de preparación de los estados financieros.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('96', 'RD', '2.1.06.01', 'TÍTULOS Y VALORES INTERNOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Esta constituida por obligaciones contraídas por el Estado con personas físicas o jurídicas residentes en el país, originadas o amparadas con la emisión de bonos y colocación de títulos, incluyendo letras del Tesoro, representadas por préstamos, aperturas de créditos y demás operaciones con títulos de valores, los cuales vencerán a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('97', 'RD', '2.1.06.02', 'TÍTULOS Y VALORES EXTERNOS A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones a corto plazo contraídas por el ente con personas naturales o jurídicas residentes en el exterior mediante la contratación de empréstitos por emisión y colocación de bonos y demás títulos de deuda pública con vencimiento a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('98', 'RD', '2.1.06.98', 'OTROS TÍTULOS Y VALORES', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa valores adeudados amparados por la emisión de títulos clasificados en las cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('99', 'RD', '2.1.07', 'ANTICIPOS FINANCIEROS Y AVANCES PRESUPUESTARIOS', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Esta representada por los adelantos de fondos que las instituciones y Poderes del Estado reciben del Tesoro, a fin de realizar pagos directos, desarrollar sus actividades, con la obligación de presentar una rendición de gastos que ejecute el presupuesto o reclasifique las variaciones a la imputación registrada con el avance. Las instituciones deben rendir estas partidas en un plazo menor de un año, con lo cual se cierra esta cuenta.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('100', 'RD', '2.1.07.01', 'FONDOS REPONIBLES A RENDIR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Esta constituido por los montos recibidos del Tesoro por las instituciones autorizadas mediante el Reglamento de Fondos Reponibles, sin imputación presupuestaria, a fin de desarrollar sus actividades y ejecutar gastos presupuestarios autorizados, los cuales estén pendientes de presentar la rendición obligatoria de los gastos realizados. Estas operaciones se realizan dentro del ejercicio presupuestario.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('101', 'RD', '2.1.07.02', 'FONDOS EN AVANCES A RENDIR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de Adelantos de Fondos que con imputación presupuestaria temporal, reciben los Poderes y organismos especiales del Estado, amparados en la Ley 47-97 y en el Reglamento para Fondos Reponibles y en Avances con la finalidad de ejecutar gastos que luego deberán ser rendidos y reclasificados de acuerdo a la ejecución real, a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('102', 'RD', '2.1.07.98', 'OTROS ANTICIPOS', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa otros anticipos financieros no contemplados en las cuentas anteriores y que deben rendirse en un plazo no mayor de doce meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('103', 'RD', '2.1.08', 'FONDOS DE TERCEROS A PAGAR', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Incluye los fondos recibidos por la Administración Central por cuenta de terceros o partidas especializadas por reclamaciones o garantías judiciales que deben ser devueltos a terceros a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('107', 'RD', '2.1.08.98', 'OTROS FONDOS DE TERCEROS', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa otros valores de terceros en poder del Tesoro y no descritas en las cuentas anteriores a pagar a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('104', 'RD', '2.1.08.01', 'FONDOS EN GARANTÍA A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las garantías constituidas a favor del Estado representadas en suma de dinero, consignada por terceras personas, entidades o funcionarios públicos en la Tesorería Nacional, bancos y otras dependencias para garantizar el fiel cumplimiento de las obligaciones contractuales, la devolución de los anticipos concedidos y el manejo de los fondos y bienes Estatales; así como para garantizar el pago de impuestos, licitaciones, permisos otorgados, o cualquier otro concepto que establecido legalmente.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('105', 'RD', '2.1.08.02', 'FONDOS EN CONSIGNACIÓN A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa valores depositados por personas físicas o jurídicas a favor de terceros, con motivo de disputas contractuales o judiciales pendientes de solución y que deberán ser reembolsado a una de las partes, al definirse legalmente el beneficiario final. Se registrara en esta cuenta, los valores a pagar a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('106', 'RD', '2.1.08.03', 'RECLAMACIONES A PAGAR A CORTO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Esta constituido por reclamaciones documentadas ante el Estado, por conceptos diversos como impuestos pagados en exceso, indemnizaciones o cualquier otro concepto reclamado y reconocido por el Estado como obligación a personas físicas y jurídicas a pagar a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('108', 'RD', '2.1.98', 'OTROS PASIVOS CORRIENTES', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el ente no contabilizadas en las cuentas anteriores, con vencimiento a corto plazo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('109', 'RD', '2.2', 'PASIVO NO CORRIENTE', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones del Ente, las cuales deberá vencerán en un plazo superior a doce meses, contados a partir de la fecha de registro.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('110', 'RD', '2.2.01', 'CUENTAS INTERNAS POR PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las deudas con proveedores, provenientes de operaciones habituales del Ente, con vencimiento posterior a los (12) doce meses de contraídas.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('111', 'RD', '2.2.02', 'CUENTAS EXTERNAS POR PAGAR LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Comprende las deudas con proveedores en el exterior del país, provenientes de operaciones habituales del ente público, con vencimiento posterior a los (12) doce meses de contraídas.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('112', 'RD', '2.2.03', 'DOCUMENTOS INTERNOS POR PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa obligaciones del Ente, reconocidas mediante documentos tales como letras, pagares y otros documentos de carácter negociables, provenientes de transacciones vinculadas con las operaciones normales del mismo, con acreedores nacionales y cuyo vencimiento es posterior a los (12) doce meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('113', 'RD', '2.2.04', 'DOCUMENTOS EXTERNOS POR PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones del Ente reconocidas por documentos, provenientes de transacciones con el Sector Externo, vinculadas con las operaciones normales del mismo, cuyo vencimiento es posterior a los (12) meses de contraídas.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('114', 'RD', '2.2.05', 'PRESTAMOS INTERNOS POR PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Estado mediante operaciones de crédito público con personas naturales o jurídicas residentes en el país, con vencimiento en un plazo superior a los (12) doce meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('115', 'RD', '2.2.05.01', 'PRESTAMOS INTERNOS A PAGAR AL SECTOR PRIVADO A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones de pago que tiene el Ente por concepto de préstamos recibidos del Sector Privado y que deberá pagar en un plazo mayor a un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('116', 'RD', '2.2.05.02', 'PRESTAMOS INTERNOS A PAGAR AL SECTOR PUBLICO A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones de pago que tiene el Ente por concepto de préstamos recibidos de otras áreas del Sector Público y que deberá pagar en un plazo mayor a un año.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('117', 'RD', '2.2.06', 'PRESTAMOS EXTERNOS POR PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Estado mediante operaciones de crédito público con personas naturales o jurídicas residentes en el exterior del país, con vencimiento en un plazo superior a los (12) doce meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('118', 'RD', '2.2.06.01', 'PRESTAMOS EXTERNOS A PAGAR AL SECTOR PRIVADO A L. PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Estado mediante operaciones de crédito público con personas naturales o jurídicas residentes en el exterior del país, pertenecientes al Sector Privado con vencimiento en un plazo superior a los (12) doce meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('119', 'RD', '2.2.06.02', 'PRESTAMOS EXTERNOS A PAGAR A ORGANISMOS INTERNACIONALES A L. PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Estado mediante operaciones de financiamientos recibidos de organismos internacionales de crédito, y cuyo vencimiento supera los doce (12) meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('120', 'RD', '2.2.06.03', 'PRESTAMOS EXTERNOS A PAGAR A GOBIERNOS EXTRANJEROS A L. P.', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Estado mediante operaciones de financiamientos recibidos directamente de Gobiernos extranjeros y que el Estado deberá saldar en un plazo mayor de doce (12) meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('121', 'RD', '2.2.07', 'TÍTULOS Y VALORES A PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Estado, con personas naturales o jurídicas residentes en el país y/o el exterior, por concepto de operaciones de crédito público derivados de la colocación de bonos y demás títulos de valores, con vencimiento superior a doce meses contados a partir de la fecha de preparación de los estados financieros.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('122', 'RD', '2.2.07.01', 'TÍTULOS Y VALORES INTERNOS A PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Ente, con personas naturales o jurídicas residentes en el país, por concepto de operaciones de crédito publico derivado de la colocación de bonos y/o títulos de valores y cuyo vencimiento es superior a doce (12) meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('123', 'RD', '2.2.07.02', 'TÍTULOS Y VALORES EXTERNOS A PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Ente, con personas naturales o jurídicas residentes en el exterior, por concepto de operaciones de crédito público derivado de la colocación de bonos y/o títulos de valores y cuyo vencimiento es superior a doce (12) meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('124', 'RD', '2.2.07.98', 'OTROS TÍTULOS A PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa obligaciones generadas por colocación de bonos o títulos de valores negociables, no contemplados en las cuentas anteriores y que deberán ser pagados en un plazo mayor a doce (12) meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('125', 'RD', '2.2.08', 'FONDOS A RENDIR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa los adelantos financieros recibidos de la Tesorería Nacional por las instituciones y que no serán rendidos dentro del periodo presupuestario vigente, es decir, permanecen abiertos como fondos disponibles y su ejecución presupuestaria será en el siguiente periodo. Esto aplica para instituciones de emergencias, hospitales y/o seguridad.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('126', 'RD', '2.2.09', 'FONDOS DE TERCEROS A PAGAR A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Incluye los fondos recibidos por la Administración Central por cuenta de terceros o partidas especializadas por reclamaciones o garantías judiciales que deben ser devueltos a terceros en un plazo mayor a los doce (12) meses.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('127', 'RD', '2.2.10', 'PASIVO DIFERIDO A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Son ingresos anticipados sobre obligaciones que deberán cumplirse en ejercicios futuros.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('128', 'RD', '2.2.98', 'OTROS PASIVOS NO CORRIENTES', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las obligaciones contraídas por el Estado, con personas naturales o jurídicas residentes en el país y el exterior, con vencimiento superior a doce meses contados a partir de la fecha de registro y que no han sido contempladas en las sub cuentas anteriormente descritas.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('129', 'RD', '2.2.99', 'PREVISIONES Y RESERVAS TÉCNICAS A LARGO PLAZO', 'PA', 'D', 'false', 'false', '0.00', '0.00', 'Expresan estimaciones de contingencias que pueden afectar el patrimonio del ente, elevar el monto de la deuda pública, como consecuencia de posibles cálculos inciertos con incidencia en los resultados de próximos ejercicios y constitución de reservas estimadas sobre la base de cálculos actuariales.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('130', 'RD', '3', 'PATRIMONIO', 'PT', 'G', 'false', 'false', '0.00', '0.00', 'Representa la diferencia entre el monto del activo y del pasivo del Estado, el cual incluye el resultado de las operaciones de ejercicios anteriores y el ejercicio vigente.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('131', 'RD', '3.1', 'HACIENDA PÚBLICA', 'PT', 'G', 'false', 'false', '0.00', '0.00', 'Representa el patrimonio constituido por la diferencia entre el monto del activo y del pasivo, el cual incluye los resultados acumulados de la gestión correspondiente a ejercicios fiscales anteriores al vigente; refleja, además, la revalorización de los bienes de uso propiedad del ente.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('132', 'RD', '3.1.01', 'CAPITAL FISCAL', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa la diferencia entre activo y pasivo del ente, a la fecha de la primera determinación del Patrimonio contable y de las variaciones incorporadas posteriormente. Constituye un registro exclusivo de la Administración Central.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('133', 'RD', '3.1.02', 'TRANSFERENCIAS Y DONACIONES DE CAPITAL RECIBIDAS', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las transferencias y contribuciones de capital recibidas del sector público y privado. Constituye un registro exclusivo de la Administración Central.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('134', 'RD', '3.1.02.02', 'DONACIONES DE CAPITAL RECIBIDAS', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las donaciones de capital recibidas por el sector público del Sector Externo. Constituye un registro exclusivo de la Administración Central.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('135', 'RD', '3.1.03', 'RESULTADOS DE LA CUENTA CORRIENTE', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto del resultado obtenido de la gestión fiscal, respecto de los ingresos y gastos corrientes en el ejercicio y en los ejercicios anteriores. Constituye un registro de uso exclusivo de la Administración Central.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('136', 'RD', '3.1.03.01', 'RESULTADOS ACUMULADOS', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto correspondiente a los resultados acumulados de la gestión fiscal respecto a los ingresos y gastos corrientes del ejercicio fiscal vigente y los anteriores a éste.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('137', 'RD', '3.1.03.02', 'RESULTADOS AFECTADOS A CONTRIBUCIONES DE DOMINIO PUBLICO', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto devengado en un período determinado para la construcción de los bienes de dominio público.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('138', 'RD', '3.1.03.03', 'SUPERÁVIT POR REVAPORIZACIÓN', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa el exceso del costo de reemplazo estimado sobre el valor depreciado u otra base de medición de los bienes de uso propiedad del Ente, sobre su costo o valor en libros.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('139', 'RD', '3.1.04', 'TRANSFERENCIAS Y DONACIONES DE CAPITAL REALIZADAS', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa las transferencias y donaciones de capital realizadas al Sector Publico, Sector Privado y el exterior con fines específicos de inversión en construcciones do obras públicas, adquisición de equipos, auxilios para inversiones financieras.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('140', 'RD', '3.1.04.01', 'TRANSFERENCIAS DE CAPITAL REALIZADAS AL SECTOR PRIVADO', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa las transferencias de capital realizadas al sector privado con la finalidad de inversiones específicas de capital.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('141', 'RD', '3.1.04.02', 'TRANSFERENCIAS DE CAPITAL REALIZADAS AL SECTOR PUBLICO', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa los recursos de capital transfereridos a instituciones publicas con fines de inversión. Esta cuenta permitirá consolidad los ingresos y egresos de capital del sector publico al compatibilizar las transferencias de las instituciones publicas receptoras y deducir las transferencias interinstitucionales.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('142', 'RD', '3.1.04.03', 'DONACIONES DE CAPITAL REALIZADAS', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa las donaciones de capital que realiza el Sector Público a las instituciones del Sector Externo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('143', 'RD', '3.2', 'PATRIMONIO INSTITUCIONAL', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto conformado por el registro de uso exclusivo de los Organismos Descentralizados, Autónomos e Instituciones de la Seguridad Social representativo de la diferencia entre activo y pasivo del ente y que constituye el capital institucional, conjuntamente con las transferencias y contribuciones de capital recibidas por el mismo y por los resultados obtenidos en la gestión corriente en el ejercicio, conjuntamente con la acumulación de resultados positivos de los sucesivos ejercicios fiscales anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('144', 'RD', '3.2.01', 'CAPITAL INSTITUCIONAL', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Registro de uso exclusivo de los Organismos Descentralizados, Autónomos e Instituciones de Seguridad Social representativo del aporte inicial para su constitución y de las variaciones posteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('145', 'RD', '3.2.02', 'TRANSFERENCIAS Y CONTRIBUCIONES DE CAPITAL RECIBIDAS', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Registro de uso exclusivo de los Organismos Descentralizados, Autónomos e Instituciones de la Seguridad Social que refleja las transferencias y contribuciones de capital recibidas del sector público.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('146', 'RD', '3.2.02.01', 'TRANSFERENCIAS DE CAPITAL', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Registro de uso exclusivo de los Organismos Descentralizados, Autónomos e Instituciones de la Seguridad Social que refleja las transferencias de capital recibidas del sector público.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('147', 'RD', '3.2.02.02', 'CONTRIBUCIONES', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Registro de uso exclusivo de los Organismos Descentralizados, Autónomos e Instituciones de la Seguridad Social que refleja las contribuciones de capital recibidas del sector público.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('148', 'RD', '3.2.03', 'RESULTADO DE LA CUENTA CORRIENTE', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Registro de uso exclusivo de los Organismos Descentralizados, Autónomos e Instituciones de la Seguridad Social que muestra el resultado obtenido de la gestión fiscal, respecto de los ingresos y gastos corrientes en el ejercicio y en los ejercicios anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('149', 'RD', '3.2.03.01', 'RESULTADO DE EJERCICIOS ANTERIORES', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Registro de uso exclusivo de los Organismos Descentralizados, Autónomos e Instituciones de la Seguridad Social que muestra el monto del resultado acumulado de la gestión correspondiente a ejercicios fiscales anteriores al vigente, el cual es positivo, cuando el monto de los ingresos es superior que el de los gastos y negativo si el monto de éstos últimos excede al de los ingresos.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('150', 'RD', '3.2.03.02', 'RESULTADO DEL EJERCICIO', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Refleja el monto del resultado de la gestión correspondiente al ejercicio fiscal vigente, el cual es positivo, cuando el monto de los ingresos es superior al de los gastos y negativo si el monto de éstos supera al de los ingresos.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('151', 'RD', '3.9', 'PATRIMONIO PUBLICO', 'PT', 'D', 'false', 'false', '0.00', '0.00', 'Registro de carácter transitorio, que refleja los movimientos positivos respecto de la construcción de bienes de dominio público, debiendo disminuirse toda vez que dichas construcciones se entreguen al uso público.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('152', 'RD', '4', 'INGRESOS', 'I', 'G', 'false', 'false', '0.00', '0.00', 'Los recursos públicos comprenden los diversos ingresos que se obtienen a través de la gestión corriente del Ente y los derivados de la potestad del Estado de establecer gravámenes, contribuciones, tasas, regalías y otros conceptos análogos; de la venta de bienes y servicios; de las rentas de la propiedad, transferencias y contribuciones recibidas del Sector Privado y el Sector Público.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('153', 'RD', '4.1', 'INGRESOS CORRIENTES', 'I', 'G', 'false', 'false', '0.00', '0.00', 'Son los que provienen de los ingresos tributarios, venta de bienes y servicios, rentas de la propiedad y de las transferencias recibidas para financiar gastos corrientes.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('154', 'RD', '4.1.01', 'INGRESOS TRIBUTARIOS', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los ingresos provenientes de los impuestos que gravan los bienes, y servicios que entran o se generan en el país. Los derechos aduaneros comprenden los aplicados según tarifa de aranceles. En esta categoría se incluyen los impuestos recaudados por importación de bienes y servicios y los derechos de exportaciones que comprenden todos los gravámenes basados en el hecho de que los bienes se envían fuera del país.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('155', 'RD', '4.1.01.01', 'IMPUESTOS SOBRE LOS INGRESOS', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los recursos recaudados por el Ente provenientes de los gravámenes aplicados a los ingresos de los contribuyentes (Persona Física o Moral) por la obtención de rentas, adquisición de propiedades, juegos de azar, alquileres, dividendos, etc.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('156', 'RD', '4.1.01.02', 'IMPUESTO SOBRE EL PATRIMONIO', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los recursos recaudados por el Ente provenientes de los gravámenes aplicados a la Propiedad y a la Transferencia de bienes muebles e inmuebles, arbitrios municipales, etc. de los contribuyentes (Persona Física o Moral). Incluye los impuestos sobre herencias, legados, regalos, etc.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('157', 'RD', '4.1.01.03', 'IMPUESTOS SOBRE MERCANCÍAS Y SERVICIOS', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los recursos recaudados por el Ente provenientes de los gravámenes aplicados a la venta de bienes o servicios industrializados, tales como el ITBIS, el Selectivo al Consumo, servicio de transporte, comunicaciones, diversiones, etc. obtenidos de los contribuyentes (Persona Física o Moral).');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('158', 'RD', '4.1.01.04', 'IMPUESTOS SOBRE EL COMERCIO EXTERIOR', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los recursos recaudados por el Ente provenientes de los gravámenes aplicados a la entrada o salidas de bienes al país, basados en tarifas y en el Arancel de aduanas, aplicables a bienes y servicios, obtenidos por los contribuyentes (Persona Física o Moral).');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('159', 'RD', '4.1.01.98', 'OTROS INGRESOS TRIBUTARIOS', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los recursos recaudados por el Ente provenientes de los gravámenes aplicados, no clasificados en categorías o cuentas anteriores.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('160', 'RD', '4.1.02', 'INGRESOS NO TRIBUTARIOS', 'I', 'G', 'false', 'false', '0.00', '0.00', 'Representa el monto de los ingresos provenientes de la gestión corriente del Ente. En esta categoría se incluyen los recaudados por venta de bienes y servicios, dividendo por inversiones, intereses, contribuciones de la Seguridad Social etc.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('161', 'RD', '4.1.02.01', 'CONTRIBUCIONES SOCIALES', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los ingresos provenientes de las retenciones para la seguridad social, tanto de los empleadores como de los empleados.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('162', 'RD', '4.1.02.02', 'VENTAS DE LAS ADMINISTRACIONES PUBLICAS', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de ingresos provenientes de ventas de mercancías y servicios del Gobierno Central.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('163', 'RD', '4.1.02.03', 'RENTAS DE LA PROPIEDAD', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de ingresos provenientes de rentas por derechos sobre bienes intangibles, alquileres, concesiones, etc.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('164', 'RD', '4.1.02.98', 'OTROS INGRESOS NO TRIBUTARIOS', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de ingresos provenientes de Aplicación contribuciones, etc.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('165', 'RD', '4.1.03', 'INGRESOS DE OPERACIONES', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de ingresos provenientes de ventas realizadas por las empresas públicas, ventas de mercancías y servicios de empresas públicas no financieras, así como la venta de servicios de empresas públicas financieras.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('166', 'RD', '4.1.03.01', 'VENTAS DE LAS EMPRESAS PUBLICAS', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de ingresos provenientes de la gestión de ventas de bienes y servicios realizada por las empresas públicas.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('167', 'RD', '4.1.04', 'TRANSFERENCIAS RECIBIDAS', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de recursos provenientes de aportes del Sector Privado Interno y del Sector Público no sujetos a contraprestación de bienes o servicios, aplicables a gastos corrientes y de capital.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('168', 'RD', '4.1.04.01', 'TRANSFERENCIAS CORRIENTES', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de recursos provenientes de aportes del Sector Privado Interno y del Sector Público no sujetos a contraprestación de bienes o servicios. Estas transferencias son contribuciones en efectivo o en especies de carácter voluntario de fuente no gubernamental Incluye las transferencias corrientes que recibe el Gobierno Central, las Instituciones Públicas Descentralizadas, Los municipios, etc. aplicables a gastos corrientes.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('169', 'RD', '4.1.05', 'DONACIONES RECIBIDAS', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de recursos provenientes de aportes del Sector Privado Externo y de Gobiernos Extranjeros no sujetos a contraprestación de bienes o servicios. Estas transferencias son contribuciones en efectivo o en especies de carácter voluntario de fuente no gubernamental, aplicables a gastos corrientes y de capital.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('170', 'RD', '4.1.05.01', 'DONACIONES CORRIENTES', 'I', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de ingresos percibidos en calidad de donación de Gobiernos Extranjeros, de Organismos Internacionales y del Sector Privado Externo, destinados a gastos corrientes. Incluye donaciones en moneda nacional o extranjera, así como en especie destinada al gasto corriente.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('171', 'RD', '5', 'GASTOS', 'G', 'G', 'false', 'false', '0.00', '0.00', 'El gasto público representa el sacrificio económico y financiero que ejecuta el Estado a través de su gestión, con el fin de adquirir y producir los bienes y servicios que le permitan lograr los objetivos sociales en beneficien de la colectividad.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('172', 'RD', '5.1.01', 'GASTOS DE CONSUMO', 'G', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las erogaciones del ente contable destinadas al pago de gastos del personal, la adquisición de materiales, suministros y servicios no personales.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('173', 'RD', '5.1.01.01', 'REMUNERACIONES', 'G', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los sueldos, salarios y otras retribuciones, bonos y complementos, beneficios socioeconómicos y otros gastos; comprende, además, el monto de los aportes patronales destinados a atender el financiamiento de la seguridad social, subvenciones, prestaciones sociales, otras indemnizaciones y cualquier otra remuneración o beneficio correspondiente a trabajadores del sector público, de conformidad con el ordenamiento jurídico y convenciones colectivas de trabajo.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('174', 'RD', '5.1.01.02', 'BIENES Y SERVICIOS', 'G', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los gastos del Ente contable originados por la prestación de servicios y la adquisición de Bienes.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('175', 'RD', '5.1.01.03', 'AMORTIZACIONES Y PROVISIONES', 'G', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de gastos devengados durante el ejercicio fiscal de la cuota periódica producto del desgaste físico de los bienes de uso, originado por el uso de los mismos, por su aplicación al proceso productivo, por la prestación de servicios o por la obsolescencia tecnológica. Los bienes se depreciarán por el lapso de vida útil o productiva, por el año de incorporación o des-incorporación al patrimonio del Ente o su utilización o fin del ciclo productivo, de acuerdo con el criterio que se adopte. A los efectos del cálculo los bienes se amortizan aplicando la cuota anual en el ejercicio fiscal.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('176', 'RD', '5.1.01.04', 'GASTOS POR CUENTAS INCOBRABLES', 'G', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de las pérdidas ocasionadas, al término del ejercicio fiscal, por la incobrabilidad de los derechos a favor del ente contable, cuyo origen sea distinto al de las rentas por recaudar.');
INSERT INTO PLAN_CUENTA_CONTABLE (ID, PAIS, CODIGO_CUENTA_CONTABLE, NOMBRE_CUENTA, TIPO_CUENTA, NIVEL_CUENTA, USA_TERCERO, CENTRO_COSTOS, PORCENTAJE_BASE, MONTO_BASE_MIN, DETALLE_CUENTA) VALUES ('177', 'RD', '5.1.02', 'GASTOS FINANCIEROS', 'G', 'D', 'false', 'false', '0.00', '0.00', 'Representa el monto de los egresos por intereses efectuados por el Ente para cubrir el costo de servicio de la deuda, tanto interna como externa, así como la cobertura de comisiones sobre préstamos, conforme las condiciones acordadas.');
